package org.ionnic.common.view.tool;import java.io.IOException;import java.util.Properties;import javax.servlet.ServletContext;import org.ionnic.common.util.ContextUtils;import org.ionnic.common.util.JsonUtils;/** * @author apple * */public class LangTool {    public ServletContext servletContext;    private Properties config;    /**     * @param key     * @return     */    public String get(String key) {        return get(key, "");    }    /**     * @param key     * @param defaultValue     * @return     */    public String get(String key, String defaultValue) {        return config.getProperty(key, defaultValue);    }    /**     * @param anything     * @throws IOException     */    public void init(Object object) throws IOException {        servletContext = (ServletContext) object;        if (servletContext == null) {            servletContext = ContextUtils.getServletContext();        }        config = (Properties) ContextUtils.getBean("viewMessageBean");    }    @Override    public String toString() {        return JsonUtils.toJson(config);    }}