package org.ionnic.common.view.tool;import java.util.Map;import javax.servlet.http.HttpServletRequest;import org.apache.velocity.tools.view.ViewToolContext;import org.ionnic.common.Security;import org.ionnic.common.util.TemplateUtils;/** * @author apple * */public class PageTool {    private ViewToolContext context;    /**     * @param name     * @param data     * @return     */    public String exec(String content, Map<String, Object> data) {        try {            data.putAll(context.getToolbox());            return TemplateUtils.renderTemplate(content, data);        } catch (Exception e) {            e.printStackTrace();        }        return "";    }    /**     * @return     */    public String getToken() {        HttpServletRequest request = context.getRequest();        return Security.generateToken(request);    }    /**     * @param urlName     * @param param     * @return     */    public String getURI(String urlName) {        HttpServletRequest req = context.getRequest();        if (!urlName.startsWith("/")) {            urlName = "/" + urlName;        }        return req.getContextPath() + urlName;    }    /**     * 请求级别的初始化函数     *     * @param obj     * @throws Exception     */    public void init(Object object) {        context = (ViewToolContext) object;        context.getRequest();    }}