package net.io.config;import java.lang.reflect.Method;import javax.servlet.http.HttpServletRequest;import org.springframework.core.annotation.AnnotationUtils;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.method.HandlerMethod;/** * @author apple * */public abstract class ActionSupport {	/**	 * @param request	 * @return	 */	public static boolean isAjax(HttpServletRequest request) {		String requestedWith = request.getHeader("X-Requested-With");		if (requestedWith == null) {			return false;		} else {			return "XMLHttpRequest".equals(requestedWith);		}	}	/**	 * @param method	 * @param request	 * @return	 */	public static boolean isJson(HandlerMethod method) {		ResponseBody anno = null;		try {			Method me = method.getMethod();			anno = AnnotationUtils.findAnnotation(me, ResponseBody.class);		} catch (Exception e) {			e.printStackTrace();		}		return anno != null;	}}