package net.io.config.support;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import net.io.config.Context;import net.io.config.util.ServletUtils;import org.springframework.web.servlet.handler.HandlerInterceptorAdapter;/** * @author apple * */public class HttpContextInterceptor extends HandlerInterceptorAdapter {	@Override	public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {		Context context = ServletUtils.getContext(request);		if (context.isCommit()) {			throw new ServletException("Already Filtered");		}		return true;	}}