package org.ionnic.config.support;import java.io.Serializable;import org.ionnic.config.util.JsonUtils;import org.springframework.util.Assert;/** * Provides the information about an expected CSRF token. * * @author Rob Winch * */public class CsrfToken implements Serializable {	private static final long serialVersionUID = -561679853276197219L;	private final String token;	private final String parameterName;	private final String headerName;	/**	 * Creates a new instance	 *	 * @param headerName	 *            the HTTP header name to use	 * @param parameterName	 *            the HTTP parameter name to use	 * @param token	 *            the value of the token (i.e. expected value of the HTTP	 *            parameter of parametername).	 */	public CsrfToken(String headerName, String parameterName, String token) {		Assert.hasLength(headerName, "headerName cannot be null or empty");		Assert.hasLength(parameterName, "para	meterName cannot be null or empty");		Assert.hasLength(token, "token cannot be null or empty");		this.headerName = headerName;		this.parameterName = parameterName;		this.token = token;	}	public String getHeaderName() {		return headerName;	}	public String getParameterName() {		return parameterName;	}	public String getToken() {		return token;	}	/**	 * @return	 */	public String toJSON() {		return JsonUtils.toJson(this);	}	@Override	public String toString() {		return getToken();	}}