package org.ionnic.core.support;import java.io.IOException;import java.io.Serializable;import java.util.Properties;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.springframework.core.io.Resource;import org.springframework.core.io.support.PropertiesLoaderUtils;@SuppressWarnings("serial")public class Config implements Serializable {	private static final Log logger = LogFactory.getLog(Config.class);	private Resource configLocation;	private String encoding;	private Properties prop;	public void initialize() throws IOException {		prop = PropertiesLoaderUtils.loadProperties(configLocation);	}	public void setConfigLocation(Resource configLocation) {		this.configLocation = configLocation;	}	public void setEncoding(String encoding) {		this.encoding = encoding;	}}