package org.ionnic.core.support;import java.util.Properties;import org.ionnic.core.support.layer.InternalConfig;import org.springframework.core.io.Resource;@SuppressWarnings("unused")public class Config {	private static Config instance;	private Properties viewConfig;	public static Config getInstance() {		return instance;	}	private String encoding;	/**	 * @param viewConfig	 * @throws Exception	 */	public Config(InternalConfig initConfig) throws Exception {		instance = this;		viewConfig = new Properties();		if (null != initConfig.getViewConfig()) {			for (Resource r : initConfig.getViewConfig()) {				viewConfig.load(r.getInputStream());			}		}	}	public static Properties getViewConfig() {		return instance.viewConfig;	}	/**	 * @param encoding	 */	public void setEncoding(String encoding) {		this.encoding = encoding;	}}