package org.ionnic.core.support;import org.springframework.core.io.Resource;public class Config {	private static Config instance;	public static Config getInstance() {		return instance;	}	public String getEncoding() {		return encoding;	}	public void setEncoding(String encoding) {		this.encoding = encoding;	}	public Resource getConfigLocation() {		return configLocation;	}	public void setConfigLocation(Resource configLocation) {		this.configLocation = configLocation;	}	private String encoding;	private Resource configLocation;}