package org.ionnic.core.support;import java.io.IOException;import java.util.Properties;import org.ionnic.core.support.config.ViewConfig;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.core.io.Resource;public class Config {	private Logger logger = LoggerFactory.getLogger(Config.class);	/**	 * default charset	 */	public static String CHARSET;	/**	 * Config instance	 */	private static Config instance;	/**	 * get instance	 * 	 * @return instance	 */	public static Config getInstance() {		return instance;	}	private ViewConfig viewConfig;	private Properties viewMap = new Properties();	/**	 * @return	 */	public ViewConfig getViewConfig() {		return viewConfig;	}	/**	 * @return	 */	public Properties getViewMap() {		return viewMap;	}	/**	 * common init method	 * 	 * @throws Exception	 * 	 * @throws IOException	 */	public void init() throws Exception {		instance = this;		initViewConfig();	}	/**	 * @param clean	 * @throws IOException	 */	public void loadViewConfig(boolean clean) throws Exception {		Resource[] configPath = getViewConfig().getConfigPath();		if (clean) {			viewMap.clear();			if (logger.isDebugEnabled()) {				logger.debug("viewConfig viewConfigMap clear() invoked.");			}		}		for (Resource resource : configPath) {			viewMap.load(resource.getInputStream());		}	}	/**	 * @param viewConfig	 */	public void setViewConfig(ViewConfig viewConfig) {		this.viewConfig = viewConfig;	}	/**	 * init the view config	 * 	 * @throws Exception	 */	private void initViewConfig() throws Exception {		loadViewConfig(true);	}}