package org.ionnic.test.db;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class Main {	private static ThreadLocal<Connection> connectionHolder = new ThreadLocal<Connection>() {		@Override		protected Connection initialValue() {			Connection conn = null;			try {				conn = DriverManager.getConnection("jdbc:mysql://127.0.0.1:3306/ad", "root", "123456");			} catch (SQLException e) {				e.printStackTrace();			}			return conn;		}	};	public static Connection getConnection() {		return connectionHolder.get();	}	public static void setConnection(Connection conn) {		connectionHolder.set(conn);	}	public static void main(String[] args) throws SQLException {		Connection con = getConnection();		con.createStatement();		System.out.println(con);	}}